import React,{useState} from "react"
import '../../App.css'
import axios from 'axios'
import {Avatar,Button,Paper,Grid,Typography,Container, TextField} from '@material-ui/core'
import useStyles from './Style'
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import Input from './Input'
import Icon from './Icon'
import {useDispatch} from 'react-redux'
import {useHistory} from 'react-router-dom'
import { NotificationContainer, NotificationManager } from 'react-notifications';
import {signin,signup} from '../../actions/auth'
import { Link } from 'react-router-dom';
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import Signinform from "./Signin"
import Signupform from "./Signup"
const initialState={firstName:'',lastName:'',email:'',mobileno:'',password:'',confirmPassword:''}
const Loginform = () =>{

  

  const classes=useStyles()
  const [showPassword,setShowPassword]=useState(false)
  const [isSignup,setIsSignup]=useState(false)
  const [formData,setFormData]=useState(initialState)
  const handleShowPassword=()=>setShowPassword((prevShowPassword)=>!prevShowPassword)
  const dispatch=useDispatch()
  const history=useHistory()

  const handleSubmit=async (e)=>{
      e.preventDefault();
      const API=axios.create({baseURL:'http://localhost:9000'})
API.interceptors.request.use((req)=>{
    if(localStorage.getItem('profile')){
        req.headers.Authorization =`Bearer ${JSON.parse(localStorage.getItem('profile')).token}`
    }
    return req
})
      if(isSignup){
   try{
    
       const {data}=await API.post('/signup',formData)
         NotificationManager.success("Success");
         setTimeout(() => {
          dispatch({type:'AUTH',data})         
                 history.push('/')
        }, 1000);
    }
    catch(err){
        if (
          err.response &&
          (err.response.status === 404 || err.response.status === 401)
        )
          NotificationManager.error(err.response.data.message);
        else NotificationManager.error("Something Went Wrong");
      };
      }
      
      else{
      try{

        const {data}=await API.post('/signin',formData)
         NotificationManager.success("Success");
          setTimeout(() => {
       dispatch({type:'AUTH',data})
        history.push('/')
       }, 1000);
    }
      catch(err){
        if (
          err.response &&
          (err.response.status === 404 || err.response.status === 401)
        )
          NotificationManager.error(err.response.data.message);
        else NotificationManager.error("Something Went Wrong");
      };
      }
  }

  const handleChange=(e)=>{
    setFormData({...formData, [e.target.name]:e.target.value})
  }

  const switchMode=()=>{
    setIsSignup((prevIsSignup)=>!prevIsSignup)
    setShowPassword(false)
}

  return(
    <>
    <NotificationContainer />
    <br/>
    <div className="form">
    <Container component="main" maxWidth={isSignup?'md':'xs'} >
      <Paper className={classes.paper} elevation={3}>
        <Avatar className={classes.avatar}>
          <AccountCircleIcon/>
        </Avatar>
        <Typography variant="h5"><b>{isSignup?'Sign Up':'Sign In'}</b></Typography>

        <Grid item xs={12}>
          {isSignup?<><Signupform/></>:
          <Signinform/>
          }
        </Grid>


        <Grid item xs={12}>
            <Grid container justify='flex-end'>
                        <Grid item>
                              <Button onClick={switchMode} style={{fontWeight:"bolder",color:"white", backgroundColor:"#1abc9c"}} fullWidth>
                                <span ><b>{isSignup ? 'Already have an account? Sign In':'If not have account, Sign Up' }</b></span>
                            </Button>
                        </Grid>
            </Grid>

        </Grid>
      </Paper>
    </Container>
    </div>
    </>
  )
}

export default Loginform
