import React,{useState} from "react"
import '../../App.css'
import axios from 'axios'
import { NotificationContainer, NotificationManager } from 'react-notifications';
import {Avatar,Button,Paper,Grid,Typography,Container, TextField} from '@material-ui/core'
import Input from './Input'
import useStyles from './Style'
import {useDispatch} from 'react-redux'
import {useHistory} from 'react-router-dom'
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import FileBase from 'react-file-base64';

const initialStateT={firstName:'',lastName:'',email:'',mobileno:'',password:'',confirmPassword:'',accounttype:''}
const initialStateS={email:'',password:''}
const Signupform=()=>{
    const [showEmail,setEmail]=useState('')
    const [Password,setPassword]=useState('')
    const [RepeatPassword,setRepeatPassword]=useState('')
    const [showType,setType]=useState(null)
    const [FirstName,setFirstName]=useState('')
    const [LastName,setLastName]=useState('')

    const [ProfilePicture, setProfilePicture]=useState('')

    const classes=useStyles()
    const [showPassword,setShowPassword]=useState(false)
    const [formData,setFormData]=useState(initialStateT)
    const dispatch=useDispatch()

    const [page1,setpage1]=useState(false)
    const [page2,setpage2]=useState(false)
    const [page3,setpage3]=useState(false)

    console.log(showEmail,Password,RepeatPassword,showType)
  const history=useHistory()
    const handleChange=(e)=>{
        setFormData({...formData, [e.target.name]:e.target.value})
      }

      const handleShowPassword=()=>setShowPassword((prevShowPassword)=>!prevShowPassword)

      const handleNext=()=>{
        
          if(page1==false && page2==false){
            
            if(Password==RepeatPassword){
                
                setpage1(true)
                if(showType=='Student'){
                    setpage3(true)
                }
            }
            else{
                //console.log("Yahan tak pahuche else m")
            }
               
          }
      }

      const handleSubmit=async (e)=>{
        e.preventDefault();
        const API=axios.create({baseURL:'http://localhost:9000'})
  API.interceptors.request.use((req)=>{
      if(localStorage.getItem('profile')){
          req.headers.Authorization =`Bearer ${JSON.parse(localStorage.getItem('profile')).token}`
      }
      return req
  })
        
        
  
        try{
  
          const {data}=await API.post('/signin',formData)
           NotificationManager.success("Success");
            setTimeout(() => {
         dispatch({type:'AUTH',data})
          history.push('/')
         }, 1000);
      }
        catch(err){
          if (
            err.response &&
            (err.response.status === 404 || err.response.status === 401)
          )
            NotificationManager.error(err.response.data.message);
          else NotificationManager.error("Something Went Wrong");
        };
        
    }

    console.log(showEmail,Password,RepeatPassword,showType,page1)

    return(
        <>
            <form className={classes.form} onSubmit={handleSubmit}>
            
                {page1?
                <>
                   <Grid container spacing={2}>
                        <Grid item xs={8}>
                        <h3><b>Personal Information:</b></h3>
                        </Grid>

                        <Input name="ContactNumber" label="Contact Number" handleChange={(event)=>setEmail(event.target.value)} type="number"/>

                        
                        <Grid item xs={8}>
                        <h5> Profile Picture </h5>
                        </Grid>

                        <Grid item xs={8}>
                            <div className={classes.fileInput}><FileBase className="FileBase" type="file" multiple={false} onDone={({ base64 }) => setProfilePicture(base64 )} /></div>
                        </Grid>
                        <Grid item xs={4}>
                            <Avatar alt="Remy Sharp" src={ProfilePicture} />
                        </Grid>

                        
                        

                    </Grid> 

                    
                </>
                :
                <>
                    <Grid container spacing={2}>

                        <Grid item xs={8}>
                        <h3>Registeration:</h3>
                        </Grid>
                    
                        <Input name="firstName" label="First Name" handleChange={(event)=>setFirstName(event.target.value)} autoFocus half/>
                        <Input name="lastName" label="Last Name" handleChange={(event)=>setLastName(event.target.value)} half/>
                        <Input name="email" label="Email Address" handleChange={(event)=>setEmail(event.target.value)} type="email"/>
                        <Input name="password" half label="Password" handleChange={(event)=>setPassword(event.target.value)} type={showPassword?"text":"password"} handleShowPassword={handleShowPassword}/>
                        <Input name="confirmPassword" half label="Repeat Password" handleChange={(event)=>setRepeatPassword(event.target.value)} type="password"/>

                        <FormControl component="fieldset">
                            <FormLabel component="legend"><b>Account Type:</b></FormLabel>
                                <RadioGroup row aria-label="gender" name="accounttype" onChange={(event)=>setType(event.target.value)}>
                                    <FormControlLabel value="Tutor" control={<Radio />} label="Tutor" />
                                    <FormControlLabel value="Student" control={<Radio />} label="Student" />
                                </RadioGroup>
                        </FormControl>

                        <Grid container spacing={3}>
                            <Grid item xs={12}>
                                
                                <Button fullWidth variant="contained" color="primary" className={classes.submit} onClick={()=>handleNext()} >
                                    Next
                                </Button>

                            </Grid>
                        </Grid>
                    </Grid>
                </>
                }
                

            
            </form>
        </>
    )
}

export default Signupform
