import React,{useState} from "react"
import '../../App.css'
import axios from 'axios'
import { NotificationContainer, NotificationManager } from 'react-notifications';
import {Avatar,Button,Paper,Grid,Typography,Container, TextField} from '@material-ui/core'
import Input from './Input'
import useStyles from './Style'
import {useDispatch} from 'react-redux'
import {useHistory} from 'react-router-dom'
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import FileBase from 'react-file-base64';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';

import Select from '@mui/material/Select';

const initialStateT={firstName:'',lastName:'',email:'',mobileno:'',password:'',confirmPassword:'',accounttype:''}
const initialStateS={email:'',password:''}
const Signupform=()=>{
    const [showEmail,setEmail]=useState('')
    const [Password,setPassword]=useState('')
    const [RepeatPassword,setRepeatPassword]=useState('')
    const [showType,setType]=useState(null)
    const [FirstName,setFirstName]=useState('')
    const [LastName,setLastName]=useState('')
   

    const [PIN,setPIN]=useState('')
    const [State, setStates] = React.useState('');
    const [City, setCity] = React.useState('');

    const [Educational,setEducation]=useState('')
    const [Experience,setExperience]=useState('')
    const [Teaches,setTeaches]=useState('')

    const [EduExp,setEduExp]=useState('')
    const [tutExp,settutExp]=useState('')

    const [ProfilePicture, setProfilePicture]=useState('')

    const classes=useStyles()
    const [showPassword,setShowPassword]=useState(false)
    const [formData,setFormData]=useState(initialStateT)
    const dispatch=useDispatch()

    const [page1,setpage1]=useState(false)
    const [page2,setpage2]=useState(false)
    const [page3,setpage3]=useState(false)

    

    console.log(showEmail,Password,RepeatPassword,showType)
  const history=useHistory()
    
   
      

      const handleShowPassword=()=>setShowPassword((prevShowPassword)=>!prevShowPassword)

      const handleNext=()=>{
        
          if(page1==false && page2==false){
            
            if(Password==RepeatPassword){
                
                setpage1(true)
                if(showType=='Student'){
                    setpage3(true)
                }
            }
            else{
                //console.log("Yahan tak pahuche else m")
            }
               
          }
      }

      const handleSubmit=async (e)=>{
        e.preventDefault();
        const API=axios.create({baseURL:'http://localhost:9000'})
  API.interceptors.request.use((req)=>{
      if(localStorage.getItem('profile')){
          req.headers.Authorization =`Bearer ${JSON.parse(localStorage.getItem('profile')).token}`
      }
      return req
  })
        
        
  
        try{
  
          const {data}=await API.post('/signin',formData)
           NotificationManager.success("Success");
            setTimeout(() => {
         dispatch({type:'AUTH',data})
          history.push('/')
         }, 1000);
      }
        catch(err){
          if (
            err.response &&
            (err.response.status === 404 || err.response.status === 401)
          )
            NotificationManager.error(err.response.data.message);
          else NotificationManager.error("Something Went Wrong");
        };
        
    }

    console.log(showEmail,Password,RepeatPassword,showType,page1)

    return(
        <>
            <form className={classes.form} onSubmit={handleSubmit}>
            
                {page1?
                <>
                   <Grid container spacing={2}>
                        <Grid item xs={8}>
                        <h3><b>Personal Information:</b></h3>
                        </Grid>

                        <Input name="ContactNumber" label="Contact Number" handleChange={(event)=>setEmail(event.target.value)} type="number"/>

                        
                        <Grid item xs={8}>
                        <h5> Profile Picture </h5>
                        </Grid>

                        <Grid item xs={8}>
                            <div className={classes.fileInput}><FileBase className="FileBase" type="file" multiple={false} onDone={({ base64 }) => setProfilePicture(base64 )} /></div>
                        </Grid>
                        <Grid item xs={4}>
                            <Avatar alt="Remy Sharp" src={ProfilePicture} />
                        </Grid>

                        <Grid item xs={4}>
                            <FormControl fullWidth>
                                <InputLabel id="demo-simple-select-label">State</InputLabel>
                                <Select
                                labelId="demo-simple-select-label"
                                id="demo-simple-select"
                                value={State}
                                label="State"
                                onChange={(event)=>{setStates(event.target.value)}}
                                >
                                <MenuItem value="Delhi">Delhi</MenuItem>
                                <MenuItem value="Rajasthan">Rajasthan</MenuItem>
                                <MenuItem value="MP">Madhya Pradesh</MenuItem>
                                <MenuItem value="UP">Uttar Pradesh</MenuItem>
                                </Select>
                            </FormControl>
                        </Grid>

                        <Grid item xs={4}>
                                <FormControl fullWidth>
                                    <InputLabel id="demo-simple-select-label">City</InputLabel>
                                    <Select
                                    labelId="demo-simple-select-label"
                                    id="demo-simple-select"
                                    value={City}
                                    label="City"
                                    onChange={(event)=>{setCity(event.target.value)}}
                                    >
                                        
                                        {State === "MP" && (
                                        <>
                                            <MenuItem value="Gwalior">Gwalior</MenuItem>
                                            <MenuItem value="Indore">Indore</MenuItem>
                                            <MenuItem value="Bhopal">Bhopal</MenuItem>
                                            <MenuItem value="Jabalpur">Jabalpur</MenuItem>
                                            <MenuItem value="Ujjain">Ujjain</MenuItem>
                                            <MenuItem value="Ratlam">Ratlam</MenuItem>
                                            <MenuItem value="Bhind">Bhind</MenuItem>
                                            <MenuItem value="Chhindwara">Chhindwara</MenuItem>

                                            </>
                                            
                                        )}
                                        {State === "UP" && (
                                        <>
                                            <MenuItem value="Agra">Agra</MenuItem>
                                            <MenuItem value="Mathura">Mathura</MenuItem>
                                            <MenuItem value="Lucknow">Lucknow</MenuItem>
                                            <MenuItem value="Kanpur">Kanpur</MenuItem>
                                            <MenuItem value="Gorakhpur">Gorakhpur</MenuItem>
                                            <MenuItem value="Varanasi">Varanasi</MenuItem>
                                            <MenuItem value="Pryagraj">Pryagraj</MenuItem>
                                            

                                            </>
                                            
                                        )}
                                        {State === "Rajasthan" && (
                                        <>
                                            <MenuItem value="Agra">Sriganganagar</MenuItem>
                                            <MenuItem value="Mathura">Bikaner</MenuItem>
                                            <MenuItem value="Lucknow">Jaipur</MenuItem>
                                            <MenuItem value="Kanpur">Alwar</MenuItem>
                                            <MenuItem value="Gorakhpur">Jodhpur</MenuItem>
                                            <MenuItem value="Varanasi">Udaipur</MenuItem>
                                            <MenuItem value="Pryagraj">Kota</MenuItem>
                                            

                                            </>
                                            
                                        )}
                                        {State === "Delhi" && (
                                        <>
                                            <MenuItem value="Chandni Chowk">Chandni Chowk</MenuItem>
                                            <MenuItem value="North East Delhi">North East Delhi</MenuItem>
                                            <MenuItem value="East Delhi">East Delhi</MenuItem>
                                            <MenuItem value="New Delhi">New Delhi</MenuItem>
                                            <MenuItem value="North West Delhi">North West Delhi</MenuItem>
                                            <MenuItem value="West Delhi">West Delhi</MenuItem>
                                            <MenuItem value="South Delhi">South Delhi</MenuItem>
                                            

                                            </>
                                            
                                        )}
                                    </Select>
                                </FormControl>
                        </Grid>

                        <Grid item xs={4}>
                        <Input name="Pin" label="PIN Code" handleChange={(event)=>setPIN(event.target.value)} type="number"/>
                        </Grid>

                        <Grid item xs={4}>
                            <FormControl fullWidth>
                                <InputLabel id="demo-simple-select-label">Education Status</InputLabel>
                                <Select
                                labelId="demo-simple-select-label"
                                id="demo-simple-select"
                                value={Educational}
                                label="Education Status"
                                onChange={(event)=>{setEducation(event.target.value)}}
                                >
                                <MenuItem value="High School">High School</MenuItem>
                                <MenuItem value="Diploma">Diploma</MenuItem>
                                <MenuItem value="Higher Secondary">Higher Secondary</MenuItem>
                                <MenuItem value="Graduated">Graduated</MenuItem>
                                <MenuItem value="Post Graduated">Post Graduated</MenuItem>
                                <MenuItem value="Doctrate">Doctrate</MenuItem>
                                </Select>
                            </FormControl>
                        </Grid>

                        <Grid item xs={4}>
                            <FormControl fullWidth>
                                <InputLabel id="demo-simple-select-label">Teaches upto</InputLabel>
                                <Select
                                labelId="demo-simple-select-label"
                                id="demo-simple-select"
                                value={Teaches}
                                label="Teaches Upto"
                                onChange={(event)=>{setTeaches(event.target.value)}}
                                >
                                <MenuItem value="Primary School">Primary School Students </MenuItem>
                                <MenuItem value="Secondary School">Secondary School Students </MenuItem>
                                <MenuItem value="High school students">High School Sudents (9-10)</MenuItem>
                                <MenuItem value="Higher Secondary">Higher Secondary Students (11-12)</MenuItem>
                                <MenuItem value="Graduting">Graduating Students</MenuItem>
                                
                                </Select>
                            </FormControl>
                        </Grid>

                        <Grid item xs={4}>
                            <FormControl fullWidth>
                                <InputLabel id="demo-simple-select-label">Teaching Experience </InputLabel>
                                <Select
                                labelId="demo-simple-select-label"
                                id="demo-simple-select"
                                value={Experience}
                                label="Teahing Experience"
                                onChange={(event)=>{setTeaches(event.target.value)}}
                                >
                                <MenuItem value={0}>No experience </MenuItem>
                                <MenuItem value={1}>1-2 years </MenuItem>
                                <MenuItem value={2}>3-5 years</MenuItem>
                                <MenuItem value={3}>5-10 years </MenuItem>
                                <MenuItem value={4}>10+ years</MenuItem>
                                
                                </Select>
                            </FormControl>
                        </Grid>
                        

                    </Grid> 

                    
                </>
                :
                <>
                    <Grid container spacing={2}>

                        <Grid item xs={8}>
                        <h3>Registeration:</h3>
                        </Grid>
                    
                        <Input name="firstName" label="First Name" handleChange={(event)=>setFirstName(event.target.value)} autoFocus half/>
                        <Input name="lastName" label="Last Name" handleChange={(event)=>setLastName(event.target.value)} half/>
                        <Input name="email" label="Email Address" handleChange={(event)=>setEmail(event.target.value)} type="email"/>
                        <Input name="password" half label="Password" handleChange={(event)=>setPassword(event.target.value)} type={showPassword?"text":"password"} handleShowPassword={handleShowPassword}/>
                        <Input name="confirmPassword" half label="Repeat Password" handleChange={(event)=>setRepeatPassword(event.target.value)} type="password"/>

                        <FormControl component="fieldset">
                            <FormLabel component="legend"><b>Account Type:</b></FormLabel>
                                <RadioGroup row aria-label="gender" name="accounttype" onChange={(event)=>setType(event.target.value)}>
                                    <FormControlLabel value="Tutor" control={<Radio />} label="Tutor" />
                                    <FormControlLabel value="Student" control={<Radio />} label="Student" />
                                </RadioGroup>
                        </FormControl>

                        <Grid container spacing={3}>
                            <Grid item xs={12}>
                                
                                <Button fullWidth variant="contained" color="primary" className={classes.submit} onClick={()=>handleNext()} >
                                    Next
                                </Button>

                            </Grid>
                        </Grid>
                    </Grid>
                </>
                }
                

            
            </form>
        </>
    )
}

export default Signupform
