import React, { useEffect } from 'react';
import { Paper, Typography, CircularProgress, Divider,Button } from '@material-ui/core/';
import { useDispatch, useSelector } from 'react-redux';
import moment from 'moment';
import { useParams, useHistory, Link } from 'react-router-dom';
import SendIcon from '@mui/icons-material/Send';
import { getPost} from '../../actions/profileCard';
import useStyles from './style';
import CommentSection from './CommentSection';

const Post=()=>{

    const { post, posts, isLoading } = useSelector((state) => state.posts);
    const dispatch = useDispatch();
    const history = useHistory();
    const classes = useStyles();
    const { id } = useParams();

    useEffect(() => {
        console.log(id,"....../////////.........")
        dispatch(getPost(id));
    }, [id]);

    console.log(post,isLoading)

    if(post==undefined)
    {
        return(
        
        <CircularProgress size="7em" />
      
        )
    }
    else{
        return(
            <Paper style={{ margin:10, padding: '20px', borderRadius: '15px',backgroundColor:'#f5f6fa',marginTop:50 }} elevation={6}>
        <div className={classes.card}>
                    <div className={classes.section}>
                    <Typography variant="h3" component="h2">{post.name}</Typography>
    
                    
                    <Typography gutterBottom variant="body1" component="p"></Typography>
                    <Typography variant="h5">
                        <b>{post.accounttype}</b>
                    </Typography>

                    <Typography variant="h6">
                        {post.city}, {post.state}
                    </Typography>
                    
                    
                    
                    
                    
                    <Divider style={{ margin: '20px 0' }} />
                    <Typography variant="h5">
                        <b>{post.educationStatus}</b>
                    </Typography>
                    <p>{post.AboutYou} </p>
                    
                    <Divider style={{ margin: '20px 0' }} />

                    

                    <form action={`mailto:${post}`}>

                        <Typography variant="h5">
                            <b>Contact:</b>
                        </Typography>
                        <Button type="submit" variant="contained" endIcon={<SendIcon />}>
                            Mail
                        </Button>

                    </form>

                    <Divider style={{ margin: '20px 0' }} />
                    <CommentSection post={post} />
                    <Divider style={{ margin: '20px 0' }} />

                    </div>
                    <div className={classes.imageSection}>
                    <img className={classes.media} src={post.ProfileDP || 'https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png'} alt={post.title} />
                    </div>
                </div>
                </Paper>
        )
    }

    return(<></>)
   
}


export default Post
